def change_local_object_attribute(object_type_name, attribute, new_value):
    """
    find a memory object and change it's attribute
    :param object_type_name:
    :param attribute:
    :param new_value:
    :return:
    """
    global_object = gc.get_objects()
    local_object = [
        x[0] for x in global_object if isinstance(x, tuple) and len(x) == 1 and type(x[0]).__name__ == object_type_name
    ][0]
    setattr(local_object, attribute, new_value)


